package server;

import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.*;
import java.util.*;
import java.util.stream.Collectors;

public class UsersServlet extends HttpServlet {
    private final TemplateEngine engine;
    Map<String, String> users;
    List<String> likes;
    String name;
    String imageURL;
    Map<String, String> temp;

    public UsersServlet(TemplateEngine engine) {
        this.engine = engine;
        likes = new ArrayList<>();
        temp = new HashMap<>();
        users = new HashMap<>();
        users.put("Joanna", "data:image/jpeg;base64,");
        users.put("Andrew", "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcR6CWYYBprZezOw4cPNt_l3VTnwcM3Dz95zlb9mEemFybrUSDNR&usqp=CAU");
        users.put("Elizabeth", "data:image/jpeg;base64,");
        users.put("Neil", "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTeJ2JgXmP9JuCms0xCokhMSwqaubpVl-r928NezryF9uh2LEtD&usqp=CAU");

    }


    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        this.name = findName();
        this.imageURL = findImage();
        HashMap<String, Object> data = new HashMap<>();
        data.put("name", name);
        data.put("imageURL", imageURL);
        if (!name.equals("nobody left bro!")) {
            engine.render("like-page.html", data, resp);
        } else {
            engine.render("nothing.html", data, resp);
        }
        users.remove(name);
    }

    private String findName() {
        this.name = users.keySet().stream().findFirst().orElse("nobody left bro!");
        return this.name;
    }

    private String findImage() {
        this.imageURL = users.getOrDefault(name, "https://robohash.org/68.186.255.198.png");
        return this.imageURL;
    }

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        String requestURI = req.getRequestURI();
        String like = req.getParameter("like");
        String dislike = req.getParameter("dislike");
        if (like != null) likes.add(like);
        else addAgain();
        fillUsers();
        resp.sendRedirect("/users");
    }

    private void fillUsers() {
        if (users.isEmpty()) {
            for (String n : temp.keySet()) {
                users.put(n, temp.get(n));
            }
            temp.clear();
        }
    }

    public void addAgain() {
        temp.put(name, imageURL);
    }
}
